def importcodontable(path):
    txt = open(path)
    lines = txt.read()
    raw = lines.replace('      ', '.')
    raw = raw.replace('   ', '.')
    raw = raw.replace('\n', '.')
    raw = raw.split('.')
    codon_key = {}
    for item in raw: 
        tab = item.split(' ')
        codon_key[tab[0]] = tab[1]

    return codon_key

def translate(RNA, codon_key):
    codons = []
    while RNA:
        codons.append(RNA[:3])
        RNA = RNA[3:]
    
    aminoacids = []
    for codon in codons: 
        aminoacids.append(codon_key[codon])
    aminoacids = ''.join(aminoacids)
    return aminoacids
    

RNA = ''
table = importcodontable('codontable.txt')
print(translate(RNA, table))


